import { useState } from 'react'

const Statistics = (props) => {
  console.log('ðŸ‘‰Statistics')
  console.log(props)
  return (
    <>
      <h1>
        statistics
      </h1>
      <p>
        good {props}
      </p>
      <p>
        neutral {props.neutral}
      </p>
      <p>
        bad {props.bad}
      </p>
      <p>
        all {props.good + props.neutral + props.bad}      
      </p>
      <p>
        average {((props.good + props.neutral*0 + props.bad*(-1))*100)/(props.good + props.neutral + props.bad)}%
      </p>
      <p>
        positive {props.good*100/(props.good+props.neutral+props.bad)}%
      </p>
    </>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleClickGood = () => {
    console.log('ðŸ‘‰handleClickGood')
    setGood(good + 1)
  }
  const handleClickNeutral = () => {
    console.log('handleClickNeutral')
    setNeutral(neutral + 1)
  }
  const handleClickBad = () => {
    console.log('handleClickBad')
    setBad(bad + 1)
  }
  return (
    <div>
      <h1>
        give feedback
      </h1>
      <button onClick={handleClickGood}>
        good
      </button>
      <button onClick={handleClickNeutral}>
        neutral
      </button>
      <button onClick={handleClickBad}>
        bad
      </button>
      <Statistics props={{good, neutral, bad}}/>
    </div>
  )
}

export default App
